import React from 'react';
//({task, ...props})
const Task = ({task, ...props}) => {

const ActionBtn =(task) => 
    <div className = "action-btn">
        {!task.done 
        ? <p>Done</p> 
        : <p>NoteDone</p>}
    </div>;

const className = 'task ' + (task.done ? 'task-done' : '');

    return (
        <div className ={className}>
            <p>{task.title} </p>
            <ActionBtn></ActionBtn>

        </div>
    )
}
export default Task ;
