import React from 'react';
import './App.css';
import Task from './Component/Task';
import TaskInput from './Component/TaskInput';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      tasks: [
        { id: 0, title: 'Что то делай 1', done: false },
        { id: 1, title: 'Что то делай 2', done: true },
        { id: 2, title: 'Что то делай 3', done: false }
      ]
    }

  }

addTask = task => {
  this.setState(state => {
    let {tasks} = state;
    tasks.push({
      id:tasks.length !==0 ? task.length : 0,
      title : task,
      done: false

    })
  })
}

  doneTask = id =>{
    const index = this.state.tasks.map(task=> task.id).indexOf(id);
    this.setState(state => {
      let {tasks} = state;
      tasks[index].done =true ;
      return tasks;

    })
  };
  deleteTask = id =>{
    const index = this.state.tasks.map(task=> task.id).indexOf(id);
    this.setState(state => {
      let {tasks} = state;
      delete tasks[index];
      return tasks;

    })
  };


  render() {
    const { tasks } = this.state;
    const activeTasks = tasks.filter(task => !task.done);
    const doneTasks = tasks.filter(task => task.done);

    return (
      <div className="App" >
        <h1 classname="top">Action tasks : {activeTasks.length}</h1>
        {[...activeTasks, ...doneTasks].map(task => <Task
          doneTask={() => this.doneTask(task.id)}
          deleteTask={() => this.deleteTask(task.id)}
          task={task}
          key={task.id} />)}
          <TaskInput></TaskInput>
      </div>
    )
  }
}


export default App;
